mapper.select:

select 

from TOM_ACTIVITY_FEES
where ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}
  and FEE_ID = #{feeId,jdbcType=DECIMAL}

mapper.update:

update TOM_ACTIVITY_FEES


    FEE_NAME = #{feeName,jdbcType=VARCHAR},


    FEE = #{fee,jdbcType=DECIMAL},


    REMARK = #{remark,jdbcType=VARCHAR},


    STATUS = #{status,jdbcType=VARCHAR},


    CREATOR = #{creator,jdbcType=VARCHAR},


    OPERATOR = #{operator,jdbcType=VARCHAR},


    UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},


    CREATOR_ID=#{creatorId,jdbcType=TIMESTAMP}


where ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}
  and FEE_ID = #{feeId,jdbcType=DECIMAL}
  and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


update TOM_ACTIVITY_FEES
set FEE_NAME = #{feeName,jdbcType=VARCHAR},
  FEE = #{fee,jdbcType=DECIMAL},
  REMARK = #{remark,jdbcType=VARCHAR},
  STATUS = #{status,jdbcType=VARCHAR},
  CREATOR = #{creator,jdbcType=VARCHAR},
  OPERATOR = #{operator,jdbcType=VARCHAR},
  UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP},
  CREATOR_ID=#{creatorId,jdbcType=TIMESTAMP}
where ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}
  and FEE_ID = #{feeId,jdbcType=DECIMAL}
  and CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


update TOM_ACTIVITY_FEES
set STATUS = 'N'
where ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}
