mapper.select:
select * from tom_activity_property where 1=1

   and ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}


   and PACKAGE_ID=#{packageId,jdbcType=DECIMAL} 


    and EXAM_ID=#{examId,jdbcType=DECIMAL}


    and COURSE_ID=#{courseId,jdbcType=DECIMAL}


    and SORT=#{sort,jdbcType=DECIMAL}

 order by SORT asc

select DISTINCT a.* from TOM_ACTIVITY_PROPERTY a,TOM_COURSES c where a.course_id is not null and a.course_id=c.course_id
and INSTR(c.COURSE_NAME,#{courseName,jdbcType=VARCHAR})>0
and c.COURSE_ONLINE = #{courseOnline,jdbcType=VARCHAR} order by a.CREATE_TIME desc limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}

select *
from TOM_ACTIVITY_PROPERTY   where ACTIVITY_ID = #{activityId,jdbcType=DECIMAL} and pre_task=#{sort,jdbcType=VARCHAR}
and course_id is null
