mapper.select:
select 

from tom_project_resource
where resource_id = #{resourceId,jdbcType=INTEGER} and status='Y' and type=#{type,jdbcType=VARCHAR}

mapper.insert:

insert into tom_project_resource (id, project_id, resource_id, 
  create_time, creator, operator, 
  update_time, status, type,parent_project_classify_name
  )
values (#{id,jdbcType=INTEGER}, #{projectId,jdbcType=INTEGER}, #{resourceId,jdbcType=INTEGER}, 
  #{createTime,jdbcType=DATE}, #{creator,jdbcType=VARCHAR}, #{operator,jdbcType=VARCHAR}, 
  #{updateTime,jdbcType=DATE}, #{status,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, #{parentProjectClassifyName,jdbcType=VARCHAR}
  )

mapper.select:
select a.* FROM TOM_PROJECT_RESOURCE a LEFT JOIN TOM_ACTIVITY b on a.resource_id=b.activity_id LEFT JOIN TOM_ACTIVITY_PROPERTY c on a.resource_id=c.activity_id WHERE 1=1
and a.PROJECT_ID = #{projectId,jdbcType=INTEGER}
and a.RESOURCE_ID = #{resourceId,jdbcType=INTEGER}
and a.TYPE='A' and a.STATUS='Y'
and b.ACTIVITY_END_TIME > #{activityIn,jdbcType=TIMESTAMP}
and b.ACTIVITY_END_TIME < #{activityEnd,jdbcType=TIMESTAMP}
and c.EXAM_ID!=''
