mapper.select:
SELECT GD.GRADE_DIMENSION_ID AS gradeDimensionId, GD.GRADE_DIMENSION_NUMBER AS gradeDimensionNumber, GD.GRADE_DIMENSION_TYPE AS gradeDimensionType, GD.CREATOR AS creator, GD.CREATOR_ID AS creatorId, GD.GRADE_DIMENSION_NAME AS gradeDimensionName, GD.GRADE_DIMENSION_NAME_EN AS gradeDimensionNameEn, GD.UPDATE_TIME AS updateTime, GD.OPERATOR AS operator, GD.STATUS AS STATUS FROM TOM_GRADE_DIMENSION GD WHERE STATUS = 'Y'
and INSTR(GD.GRADE_DIMENSION_NAME,#{gradeDimensionName,jdbcType=VARCHAR})>0
AND GD.GRADE_DIMENSION_TYPE = #{gradeDimensionType,jdbcType=VARCHAR}
order by isnull(UPDATE_TIME)-1, UPDATE_TIME desc
limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}

SELECT COUNT(*) FROM TOM_GRADE_DIMENSION GD WHERE GD.STATUS = 'Y'
and INSTR(GD.GRADE_DIMENSION_NAME,#{gradeDimensionName,jdbcType=VARCHAR})>0
AND GD.GRADE_DIMENSION_TYPE = #{gradeDimensionType,jdbcType=VARCHAR}

SELECT GD.GRADE_DIMENSION_ID AS gradeDimensionId, GD.GRADE_DIMENSION_NUMBER AS gradeDimensionNumber, GD.GRADE_DIMENSION_TYPE AS gradeDimensionType, GD.CREATOR AS creator, GD.CREATOR_ID AS creatorId, GD.GRADE_DIMENSION_NAME AS gradeDimensionName, GD.GRADE_DIMENSION_NAME_EN AS gradeDimensionNameEn, GD.UPDATE_TIME AS updateTime, GD.OPERATOR AS operator, GD.STATUS AS STATUS from TOM_GRADE_RECORDS GR,TOM_GRADE_DIMENSION GD WHERE GD.STATUS = 'Y' AND GR.grade_dimension_id = GD.grade_dimension_id
and INSTR(GD.GRADE_DIMENSION_NAME,#{gradeDimensionName,jdbcType=VARCHAR})>0
AND GD.GRADE_DIMENSION_TYPE = #{gradeDimensionType,jdbcType=VARCHAR}
AND GRADE_DIMENSION_ID = #{gradeDimensionId,jdbcType=DECIMAL}
