mapper.insert:
insert into TOM_GRADE_RECORDS (COURSE_ID, CODE, CREATE_TIME, 
  GRADE_TYPE, GRADE_DIMENSION_ID, SCORE
  )
values (#{courseId,jdbcType=DECIMAL}, #{code,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{gradeType,jdbcType=VARCHAR}, #{gradeDimensionId,jdbcType=DECIMAL}, #{score,jdbcType=DECIMAL}
  )

mapper.select:

  	SELECT R.GRADE_DIMENSION_ID GRADE_DIMENSION_ID,R.SCORE SCORE,
  	D.GRADE_DIMENSION_NAME GRADE_DIMENSION_NAME ,D.GRADE_DIMENSION_NAME_EN FROM TOM_GRADE_RECORDS R 
			LEFT JOIN TOM_GRADE_DIMENSION D ON D.GRADE_DIMENSION_ID = R.GRADE_DIMENSION_ID 
			WHERE 1=1  

        and R.COURSE_ID = #{courseId,jdbcType=DECIMAL}


        and GRADE_TYPE = #{gradeType,jdbcType=VARCHAR}


        and code = #{code,jdbcType=VARCHAR}
