mapper.select:

select DISTINCT a.*,c.sort
from TOM_EXAM_ANSWER_DETAILS a,TOM_EXAM b,TOM_EXAM_QUESTION c,TOM_TOPIC d
where a.EXAM_ID=b.EXAM_ID and b.EXAM_PAPER_ID=c.EXAM_PAPER_ID and
a.TOPIC_ID=d.TOPIC_ID and d.QUESTION_TYPE=c.QUESTION_TYPE

	and a.EXAM_ID= #{examId,jdbcType=DECIMAL}


	and a.CODE=#{code,jdbcType=VARCHAR}


	and a.STATUS=#{status,jdbcType=VARCHAR}

order by isnull(c.SORT)-1, c.SORT asc


select *
from TOM_EXAM_ANSWER_DETAILS
where EXAM_ID= #{examId,jdbcType=DECIMAL} and CODE=#{code,jdbcType=VARCHAR}
and TOPIC_ID=#{topicId,jdbcType=DECIMAL}

mapper.delete:

delete from TOM_EXAM_ANSWER_DETAILS
where 1=1

	and EXAM_ID=#{examId,jdbcType=DECIMAL}


	and CODE=#{code,jdbcType=VARCHAR}


	and STATUS=#{status,jdbcType=VARCHAR}

mapper.select:

select DISTINCT a.*,c.sort
from TOM_EXAM_ANSWER_DETAILS a,TOM_EXAM b,TOM_EXAM_QUESTION c,TOM_TOPIC d
where a.EXAM_ID=b.EXAM_ID and b.EXAM_PAPER_ID=c.EXAM_PAPER_ID and
a.TOPIC_ID=d.TOPIC_ID and d.QUESTION_TYPE=c.QUESTION_TYPE

	and a.EXAM_ID= #{examId,jdbcType=DECIMAL}


	and a.CODE=#{code,jdbcType=VARCHAR}


	and a.STATUS=#{status,jdbcType=VARCHAR}

order by a.code asc,a.topic_id
