mapper.select:

select a.code code,a.EXAM_ID examId,a.EMP_NAME empName,a.GRADE_STATE gradeState,a.EXAM_COUNT examCount,a.TOTAL_POINTS totalPoints,
c.full_mark fullMark,c.pass_mark passMark
from TOM_EXAM_SCORE a,tom_exam b,tom_exam_paper c 
where a.EXAM_ID = b.exam_id and b.exam_paper_id = c.exam_paper_id
and a.GRADE_STATE != 'D' and a.EXAM_ID = #{examId,jdbcType=DECIMAL}
limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}


select * from TOM_EXAM_SCORE where 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}


	and GRADE_STATE = #{gradeState,jdbcType=VARCHAR}

mapper.delete:

delete from TOM_EXAM_SCORE
where 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}


	and GRADE_STATE = #{gradeState,jdbcType=VARCHAR}

mapper.select:
select t.* from tom_exam_score t where
t.code=#{empcode,jdbcType=VARCHAR} and
t.exam_id=#{examId,jdbcType=VARCHAR}

select t.* from tom_exam_score t where t.grade_state!='P' and
t.code=#{empcode,jdbcType=VARCHAR} and
t.exam_id=#{examId,jdbcType=VARCHAR}

select count(*) from TOM_EXAM_SCORE where 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and EMP_NAME = #{empName,jdbcType=VARCHAR}


	and CODE = #{code,jdbcType=VARCHAR}

select * from TOM_EXAM_SCORE where GRADE_STATE !='P'

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and EMP_NAME = #{empName,jdbcType=VARCHAR}


	and CODE = #{code,jdbcType=VARCHAR}


select * from TOM_EXAM_SCORE
where 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}

order by CREATE_TIME desc
limit 0,1

SELECT GRADE_STATE,MAX(TOTAL_POINTS) FROM TOM_EXAM_SCORE
WHERE 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}


	and CREATE_TIME < #{activityEndTime,jdbcType=TIMESTAMP}

select 
	 S.EXAM_ID examId,S.CODE code,S.GRADE_STATE gradeState,sum(S.retaking_exam_count)+1 totalPoints,sum(S.exam_count)  examCount,S.retaking_exam_count examTotalTime 
from 
	(
		select sc.*,ex.retaking_exam_count retaking_exam_count from tom_exam_score sc
		left join tom_exam ex on sc.exam_id = ex.exam_id
		where sc.code= #{code,jdbcType=VARCHAR}

		and grade_state != #{gradeState,jdbcType=VARCHAR}

			and ex.exam_id = #{examId,jdbcType=DECIMAL}

	)S 

group by S.exam_id

select * from TOM_EXAM_SCORE
where 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}

and EXAM_COUNT=1

SELECT * FROM TOM_EXAM_SCORE
WHERE 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}

and TOTAL_POINTS=(
SELECT MAX(TOTAL_POINTS)
where 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}
)

SELECT MAX(TOTAL_POINTS) as totalPoints,GRADE_STATE as gradeState,EXAM_COUNT as examCount,EXAM_TOTAL_TIME as examTotalTime
FROM
TOM_EXAM_SCORE
WHERE 1=1

	and EXAM_ID = #{examId,jdbcType=DECIMAL}


	and CODE = #{code,jdbcType=VARCHAR}

		SELECT
		a.`CODE` AS code,a.`NAME` AS name,a.ORGNAME AS orgName,a.ONEDEPTNAME AS oneDeptName,a.TWO_DEPT_NAME AS twoDeptName,
		a.THREE_DEPT_NAME AS threeDeptName,a.JOBNAME AS jobName,a.SECRET_EMAIL AS secretEmail,
		c.ACTIVITY_NAME AS theActivityName,c.ACTIVITY_NUMBER AS activityNumber,c.CREATE_TIME AS createTime,c.NEED_APPLY AS isRequired,
		c.NEED_APPLY AS needApply,c.APPLICATION_START_TIME AS applicationStartTime,c.ACTIVITY_START_TIME AS activityStartTime,
		c.ACTIVITY_END_TIME AS activityEndTime,c.PROTOCOL_START_TIME AS protocolStartTime,
		e.EXAM_ID AS examId,e.EXAM_NAME AS examName,e.EXAM_NUMBER AS examNumber,
		f.parent_project_classify_name AS parentProjectClassifyName,
		g.PASS_EB AS passEb
		FROM
		tom_emp a
		LEFT JOIN tom_activity_emps_relation b ON a.`CODE`=b.`CODE`
		LEFT JOIN tom_activity c ON b.ACTIVITY_ID=c.ACTIVITY_ID
		LEFT JOIN tom_activity_property d ON c.ACTIVITY_ID=d.ACTIVITY_ID
		LEFT JOIN tom_exam e ON d.EXAM_ID=e.EXAM_ID
		LEFT JOIN tom_project_resource f ON c.ACTIVITY_ID=f.resource_id
		LEFT JOIN tom_exam_paper g ON e.EXAM_PAPER_ID=g.EXAM_PAPER_ID
		WHERE 1=1
		AND b.`STATUS`='Y' AND d.EXAM_ID IS NOT NULL AND f.`status`='Y' AND f.type='A'

			and INSTR(a.CODE,#{code,jdbcType=VARCHAR})>0


       		and INSTR(a.NAME,#{name,jdbcType=VARCHAR})>0


       		and a.ORGCODE = #{orgcode,jdbcType=VARCHAR}


       		and a.ONEDEPTCODE = #{onedeptcode,jdbcType=VARCHAR}


       		and a.TWO_DEPT_CODE = #{deptcode2,jdbcType=VARCHAR}


       		and a.THREE_DEPT_CODE = #{deptcode3,jdbcType=VARCHAR}


       		and c.ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}


       		and c.CREATE_TIME < #{examTotalTime,jdbcType=TIMESTAMP}


       		and c.ACTIVITY_END_TIME > #{activityIn,jdbcType=TIMESTAMP}


       		and c.ACTIVITY_END_TIME < #{activityEnd,jdbcType=TIMESTAMP}


			and c.NEED_APPLY = #{needApply,jdbcType=VARCHAR}


       		and INSTR(e.EXAM_NAME,#{examName,jdbcType=VARCHAR})>0


			and f.PROJECT_ID = #{projectId,jdbcType=DECIMAL}

      	limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}

		SELECT
		COUNT(a.`CODE`)
		FROM
		tom_emp a
		LEFT JOIN tom_activity_emps_relation b ON a.`CODE`=b.`CODE`
		LEFT JOIN tom_activity c ON b.ACTIVITY_ID=c.ACTIVITY_ID
		LEFT JOIN tom_activity_property d ON c.ACTIVITY_ID=d.ACTIVITY_ID
		LEFT JOIN tom_exam e ON d.EXAM_ID=e.EXAM_ID
		LEFT JOIN tom_project_resource f ON c.ACTIVITY_ID=f.resource_id
		WHERE 1=1
		AND b.`STATUS`='Y' AND d.EXAM_ID IS NOT NULL AND f.`status`='Y' AND f.type='A'

			and INSTR(a.CODE,#{code,jdbcType=VARCHAR})>0


       		and INSTR(a.NAME,#{name,jdbcType=VARCHAR})>0


       		and a.ORGCODE = #{orgcode,jdbcType=VARCHAR}


       		and a.ONEDEPTCODE = #{onedeptcode,jdbcType=VARCHAR}


       		and a.TWO_DEPT_CODE = #{deptcode2,jdbcType=VARCHAR}


       		and a.THREE_DEPT_CODE = #{deptcode3,jdbcType=VARCHAR}


       		and c.ACTIVITY_ID = #{activityId,jdbcType=DECIMAL}


       		and c.CREATE_TIME < #{examTotalTime,jdbcType=TIMESTAMP}


       		and c.ACTIVITY_END_TIME > #{activityIn,jdbcType=TIMESTAMP}


       		and c.ACTIVITY_END_TIME < #{activityEnd,jdbcType=TIMESTAMP}


			and c.NEED_APPLY = #{needApply,jdbcType=VARCHAR}


       		and INSTR(e.EXAM_NAME,#{examName,jdbcType=VARCHAR})>0


			and f.PROJECT_ID = #{projectId,jdbcType=DECIMAL}
