mapper.select:
select
from TOM_QUESTION_CLASSIFICATION where PARENT_CLASSIFICATION_ID = #{parentClassificationId,jdbcType=DECIMAL}
and INSTR(QUESTION_CLASSIFICATION_NAME,#{questionClassificationName,jdbcType=VARCHAR})>0

SELECT * from TOM_QUESTION_CLASSIFICATION where
PARENT_CLASSIFICATION_ID = #{parentClassificationId,jdbcType=DECIMAL}

	and
	INSTR(QUESTION_CLASSIFICATION_NAME,#{questionClassificationName,jdbcType=VARCHAR})>0


order by UPDATE_TIME desc
limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}

SELECT * FROM TOM_QUESTION_CLASSIFICATION where QUESTION_CLASSIFICATION_ID in (${ids})
and INSTR(QUESTION_CLASSIFICATION_NAME,#{example.questionClassificationName,jdbcType=DECIMAL})>0
order by UPDATE_TIME desc limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}
