mapper.select:
SELECT * FROM tom_admin where 1=1 and code!=#{superCode,jdbcType=VARCHAR} and admin_id!=0
and INSTR(name,#{name,jdbcType=VARCHAR})>0
order by update_time desc limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}

mapper.update:
update TOM_ADMIN
set
STATUS = #{status,jdbcType=VARCHAR},
update_time =
#{updateTime,jdbcType=VARCHAR}
where ADMIN_ID = #{adminId}
