mapper.select:
select * from TOM_DEPT where 1=1 AND HRCANCELED = 'N'

	and INSTR(NAME,#{name,jdbcType=VARCHAR})>0

limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}

