mapper.select:

SELECT tg.*,tog.name,tog.pk_org,tog.enablestate FROM
tom_grp_org_relation tg,tom_org tog where tg.code=tog.code and
tg.STATUS='Y'

	and grp_Code = #{example.grpCode,jdbcType=VARCHAR}

limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}

mapper.insert:
insert into TOM_GRP_ORG_RELATION (CODE, GRP_CODE, ADMINISTRATOR,
PHONE, EMAIL, STATUS
)
values (#{code,jdbcType=VARCHAR}, #{grpCode,jdbcType=VARCHAR},
#{administrator,jdbcType=VARCHAR},
#{phone,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}
)
