mapper.select:
select u.e_number,e.code,e.name,e.email,e.phone,e.status from tom_user_log e,tom_user_info u
where
u.code=e.code and e.code= #{code,jdbcType=VARCHAR}

select
FROM TOM_USER_INFO
order by cast(LEARNING_TIME as signed integer) desc limit #{startNum,jdbcType=DECIMAL},#{endNum,jdbcType=DECIMAL}
